DECLARE
  user_name text;
BEGIN
  SELECT COALESCE("Nome", "Name", "Email") INTO user_name
  FROM "Users"
  WHERE "Row ID" = user_id
  LIMIT 1;

  RETURN COALESCE(user_name, 'Usurio');
END;

